export default function getWorldFieldOfView(camera, renderer, distance) {
  const fov = camera.fov;

  let vFOV = fov * Math.PI / 180;        // convert vertical fov to radians
  let height = 2 * Math.tan(vFOV / 2) * distance; // visible height
  let aspect = renderer.domElement.clientWidth / renderer.domElement.clientHeight;
  let width = height * aspect;

  return { width: width, height: height, maxSize: width > height ? width : height, minSize: width > height ? height : width };
}